var App = {
	el: {
		body: $('body'),
		editor: $('#Editor'),
		view: $('#View'),
		download: $('.js-download'),
		dropzone: $('.js-dropzone'),
		overlay: {
			newFile: $('.js-overlay--new-file')
		}
	},
	state: {
		download: false
	}
};

var Storage = localStorage.getItem('aster_content') !== '' ? localStorage.getItem('aster_content') : '',
	Document = {
		init: function(){
			Reqs.markdown();
			Reqs.highlight();

			this.bindings()
			this.getStorage();
		},
		bindings: function(){
			App.el.editor.on('keyup', 
				Document.throttle(function(event){
					App.el.body.trigger('updated:editor');
					if (App.state.download == true) File.setDownload();
					DOM.menu.close();
			}, 1000));
		},
		clearEditor: function(){
			App.el.editor.empty();
		},
		getStorage: function(){
			if (Storage !== null) {
				var content = localStorage.getItem('aster_content');

				Document.updateView( marked(content) );
				App.el.editor.prop('value', content);

				Document.addStorage();
			} else {
				Document.addStorage();
			}
		},
		addStorage: function(){
			$('body').on('updated:editor', function(){
				var content = App.el.editor.prop('value');

				localStorage.setItem('aster_content', content);

				Document.updateView(content);
			});
		},
		updateView: function(data){
			App.el.view.html(marked(data));

			this.highlight();
		},
		highlight: function(){
			$('pre code').each(function(i, e) {
		        hljs.highlightBlock(e);
		    });
		},
		throttle: function(fn, threshhold, scope) {
			threshhold || (threshhold = 250);

			var last,
			  	deferTimer;

			return function () {
				var context = scope || this;

				var now = +new Date,
				    args = arguments;

				if (last && now < last + threshhold) {
					// hold on to it
					clearTimeout(deferTimer);
					deferTimer = setTimeout(function () {
						last = now;
						fn.apply(context, args);
					}, threshhold);
				} else {
					last = now;
					fn.apply(context, args);
				}
			}
		}
	}

var $body = $('body'),
	$window = $(window),
	DOM = {
	init: function(){
		this.panes();
		this.menu.init();
		this.file.init();
		this.overlays();

		$window.load(function(){
			DOM.pageLoad();
		});
	},
	pageLoad: function(){
		setTimeout(function(){
			$body.css('opacity', 1).addClass('is-loaded').attr('style', '');
		}, 500);
	},
	// the view switching
	panes: function(){
		var panes = $('.pane');

		DOM.title(); // set title

		panes.on('click', function(){
			if ($(this).hasClass('is-active')) {
				return;
			} else {
				panes.toggleClass('is-active');
				DOM.title();
			}
		});
	},
	// edits page title
	title: function(){
		if ($('.pane__left').hasClass('is-active')){
			document.title = 'Aster | Editing';
		} else if ($('.pane__right').hasClass('is-active')) {
			document.title = 'Aster | Viewing';
		}
	},
	menu: {
		init: function(){
			$('.js-menu').on('click', function(e){
				e.preventDefault();
				DOM.menu.open();
			});
		},
		open: function(){
			$body.toggleClass('menu--is-open');
		},
		close: function(){
			$body.removeClass('menu--is-open');
		}
	},
	overlays: function(){
		$('.js-closeOverlay').on('click', function(e){
			var $overlay = $(this).closest('.js-overlay');
			$overlay.addClass('is-hiding');
			setTimeout(function(){
				$overlay.removeClass('is-hiding is-visible');
			}, 300);
		});
	},
	file: {
		init: function(){
			var $new_toggle = $('.js-new_toggle'),
				$new_form = $('.js-new_form');

			$new_toggle.on('click', function(e){
				e.preventDefault();
				App.el.overlay.newFile.addClass('is-visible');
			});
			$new_form.submit(function(e){
				e.preventDefault();

				var new_post_title = $('.js-new_post_title').prop('value'),
					new_post_tags = $('.js-new_post_tags').prop('value');
					
				DOM.file.create(data);
			});
		},
		create: function(data){
			console.log(data);
		}
	}
}

var File = {
	init: function(){
		// Check for download support
		var a = document.createElement('a');
		if (typeof a.download != "undefined") {
			this.setDownload();  
			App.state.download = true;
		}
		// Add dataTransfer attr
		$.event.props.push('dataTransfer');

		// DRAG EVENTS
		var is_drag = 0;
		App.el.body
			.on('dragenter', function(e){
				is_drag++;
				e.preventDefault();
				e.stopPropagation();
			    e.dataTransfer.dropEffect = 'copy';
				App.el.dropzone.addClass('is-visible');
			})
			.on('dragleave', function(e){
				is_drag--;
				if (is_drag === 0) {
					App.el.dropzone.removeClass('is-visible');		
				}
			})
			.on('dragover dragleave', '.js-dropzone', function(e){
				e.preventDefault();
				e.stopPropagation();
			})
			.on('drop', '.js-dropzone', function(e){
				e.preventDefault();
				e.stopPropagation();
				File.processUpload(e);
			});
	},
	setDownload: function(){
		var content = App.el.editor.prop('value');
		App.el.download.attr('download', 'export.md').attr('href', 'data:text/plain,' + content);
	},
	processUpload: function(e){
		var files = e.dataTransfer.files,
			reader = new FileReader(),
			$progress = $('.js-progress');

		// reader.onerror
		// reader.onabort
		reader.onloadstart = function(e) {
			App.el.dropzone.removeClass('is-visible');
			App.el.body.addClass('file--is-loading');
		};
		reader.onprogress = function(e){
			if (e.lengthComputable) {
				var percentLoaded = Math.round((e.loaded / e.total) * 100);
				$progress.css('width', percentLoaded + '%');
			}
		}
		reader.onload = function(upload){
			Document.clearEditor();
			App.el.editor.prop('value', reader.result);
			App.el.body.trigger('updated:editor');
			App.el.body.removeClass('file--is-loading');
		};
		reader.readAsText(files[0]);
	}
}

